import React, { useState } from 'react';
import styled from 'styled-components';
import { MediumTitle } from 'style/style';
import Button from 'components/Button/Button';
import { useNavigate } from 'react-router-dom'; // Updated import

const Container = styled.div`
  /* ...styles... */
`;

const Input = styled.input`
  /* ...styles... */
`;

const QuestionContainer = styled.div`
  /* ...styles... */
`;

const ErrorText = styled.p`
  color: red;
  font-size: 1.2rem;
`;

function InputStep({ question, value, onChange, onKeyPress, type = 'text' }) {
  return (
    <>
      <MediumTitle>{question}</MediumTitle>
      <Input
        onChange={onChange}
        onKeyPress={onKeyPress}
        value={value}
        type={type}
      />
    </>
  );
}

function Register() {
  const navigate = useNavigate();
  const [name, setName] = useState(localStorage.getItem('name') || '');
  const [age, setAge] = useState(localStorage.getItem('age') || '');
  const [step, setStep] = useState(name ? 2 : 1);
  const [error, setError] = useState('');

  const validateInput = input => {
    if (!input) {
      setError(`Please enter your ${step === 1 ? 'name' : 'age'}.`);
      return false;
    }
    if (step === 2) {
      const ageValue = parseInt(input, 10);
      if (isNaN(ageValue) || ageValue <= 0 || ageValue > 120) {
        setError('Please enter a valid age.');
        return false;
      }
    }
    setError('');
    return true;
  };

  const handleNameChange = e => setName(e.target.value);
  const handleAgeChange = e => setAge(e.target.value);

  const handleClick = () => {
    if (step === 1) {
      const isValid = validateInput(name);
      if (isValid) {
        localStorage.setItem('name', name);
        setStep(2);
      }
    } else if (step === 2) {
      const isValid = validateInput(age);
      if (isValid) {
        localStorage.setItem('age', age);
        navigate('/');
      }
    }
  };

  const handleKeyPress = e => {
    if (e.key === 'Enter') {
      handleClick();
    }
  };

  return (
    <div>
      <Container>
        <QuestionContainer>
          {step === 1 ? (
            <InputStep
              question="What is your name?"
              value={name}
              onChange={handleNameChange}
              onKeyPress={handleKeyPress}
            />
          ) : (
            <InputStep
              question="What is your age?"
              value={age}
              onChange={handleAgeChange}
              onKeyPress={handleKeyPress}
              type="number"
            />
          )}
          {error && <ErrorText>{error}</ErrorText>}
          <Button onClick={handleClick} />
        </QuestionContainer>
      </Container>
    </div>
  );
}

export default React.memo(Register);
