import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

import { MediumTitle } from 'style/style';
import Button from 'components/Button/Button';

const Container = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  height: 92vh;
  width: 100vw;
  top: 0;
  left: 0;

  @media (max-width: 900px) {
    flex-direction: column;
  }
`;

const Input = styled.input`
  width: 100%;
  height: 50px;
  font-size: 2.5rem;
  text-align: center;
  border-bottom: 3px solid black;
  margin-bottom: 3rem;

  @media (max-width: 900px) {
    margin-bottom: 2rem;
  }

  @media (max-width: 576px) {
    width: 80%;
    font-size: 1.6rem;
  }
`;

const QuestionContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

function Register(props) {
  const [meditationPurpose, setMeditationPurpose] = useState('');
  const [aiResponse, setAiResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1);

  const validateInput = (input) => {
    if (!input.trim()) {
      alert('Please enter your meditation purpose.');
      return false;
    }
    return true;
  };

  const handleMeditationPurposeChange = (e) => setMeditationPurpose(e.target.value);

  const sendToOpenAI = async () => {
    if (!validateInput(meditationPurpose)) return;
    setLoading(true);

    const prompt = `Analyze and provide suggestions based on this meditation purpose: "${meditationPurpose}"`;

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-4',
          messages: [
            { role: 'system', content: 'You are a meditation assistant.' },
            { role: 'user', content: prompt },
          ],
        },
        {
          headers: {
            'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`, 
            'Content-Type': 'application/json',
          },
        }
      );

      const responseText = response.data.choices[0]?.message?.content || '';
      const parsedSentences = responseText.split('. ').map(sentence => sentence.trim()).filter(Boolean);

      localStorage.setItem('meditationPurpose', meditationPurpose);
      localStorage.setItem('meditationResponse', JSON.stringify(parsedSentences));

      setAiResponse(parsedSentences);
    } catch (error) {
      console.error('Error sending data to OpenAI:', error);
      alert('Failed to process meditation purpose. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Container>
        <QuestionContainer>
          {step === 5 ? (
            <>
              <MediumTitle>What's the purpose for your meditation?</MediumTitle>
              <Input
                onChange={handleMeditationPurposeChange}
                value={meditationPurpose}
                placeholder="Enter your purpose..."
              />
              <Button onClick={sendToOpenAI} text={loading ? "Processing..." : "Submit"} />
            </>
          ) : (
            <Button onClick={() => setStep(5)} text="Next" />
          )}
          {aiResponse && (
            <div>
              <MediumTitle>Suggestions from AI:</MediumTitle>
              <ul>
                {aiResponse.map((sentence, index) => (
                  <li key={index}>{sentence}</li>
                ))}
              </ul>
            </div>
          )}
        </QuestionContainer>
      </Container>
    </div>
  );
}

export default React.memo(Register);
