import React, { useState, useEffect } from 'react';
import styled, { css } from 'styled-components';
import PropType from 'prop-types';

import { fade } from 'style/animation';
import { SmallTitle } from 'style/style';

const Text = styled(SmallTitle)`
  color: white;
  z-index: 3;
  opacity: 0;
  animation: ${props =>
    props.playing &&
    css`
      ${fade} 4s ease-in infinite
    `};
`;
let textArr = [];


function DynamicText({ playing }) {
  const [textIdx, setTextIdx] = useState(0);
  const savedmeditationType = localStorage.getItem('meditationType');
  textArr = [
    "Inhale calmness",
    "Exhale stress",
    "Be present in this moment",
    "Feel the rhythm of your breath",
    "Let go of tension",
    "Your mind is at ease",
    "One breath at a time",
    "You are in control",
    "Breathe. Relax. Repeat.",
    "Find your inner balance"
  ];
  if (savedmeditationType === 'Unguided') { 
  textArr = [];
  }
  let textThatChanges = textArr[textIdx % textArr.length];

const savedage = localStorage.getItem('age');
let breathingtime = 4000 + savedage*20;

  useEffect(() => {
    if (playing) {
      const interval = setInterval(() => {
        setTextIdx(index => index + 1);
      }, breathingtime);
      return () => {
        clearInterval(interval);
      };
    }
  }, [playing]);

  return <Text playing={playing}>{textThatChanges}</Text>;
}

DynamicText.propType = {
  playing: PropType.bool,
};

export default DynamicText;
