import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

import { MediumTitle } from 'style/style';
import Button from 'components/Button/Button';

const Container = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  height: 92vh;
  width: 100vw;
  top: 0;
  left: 0;

  @media (max-width: 900px) {
    flex-direction: column;
  }
`;

const Input = styled.input`
  width: 100%;
  height: 50px;
  font-size: 2.5rem;
  text-align: center;
  border-bottom: 3px solid black;
  margin-bottom: 5rem;

  @media (max-width: 900px) {
    margin-bottom: 0;
  }

  @media (max-width: 576px) {
    width: 80%;
    font-size: 1.6rem;
  }
`;

const QuestionContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BackIcon = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  font-size: 2rem;
  cursor: pointer;
`;

function Register(props) {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [step, setStep] = useState(1);

  // Reset everything when the component mounts
  useEffect(() => {
    console.log('Component mounted. Resetting state to step 1.');
    setStep(1);
    setName('');
    setAge('');
    localStorage.clear(); // Clear all persistent data
  }, []);

  const validateInput = input => {
    if (!input) {
      alert(`Please enter your ${step === 1 ? 'name' : 'age'}.`);
      return false;
    }
    return true;
  };

  const handleNameChange = e => setName(e.target.value);
  const handleAgeChange = e => setAge(e.target.value);

  const handleClick = () => {
    if (step === 1) {
      if (validateInput(name)) {
        localStorage.setItem('name', name);
        setStep(2); // Move to the next step
      }
    } else if (step === 2) {
      if (validateInput(age)) {
        localStorage.setItem('age', age);
        props.history.push('/'); // Navigate to the next page
      }
    }
  };

  const handleBackToName = () => {
    setStep(1);
    setName('');
    localStorage.removeItem('name');
  };

  return (
    <div>
      <Container>
        {step === 2 && <BackIcon onClick={handleBackToName}>⬅️</BackIcon>}
        <QuestionContainer>
          {step === 1 ? (
            <>
              <MediumTitle>What is your name?</MediumTitle>
              <Input onChange={handleNameChange} value={name} />
            </>
          ) : (
            <>
              <MediumTitle>What is your age?</MediumTitle>
              <Input onChange={handleAgeChange} value={age} type="number" />
            </>
          )}
          <Button onClick={handleClick} />
        </QuestionContainer>
      </Container>
    </div>
  );
}

export default React.memo(Register);
